    def crear_base(self):
        cursor = conn.cursor()
        cadena_sql = 'CREATE TABLE Proveedores (proveeCombustible TEXT, numGlns INTEGER, costoGln INTEGER)'
        try:
            cursor.execute(cadena_sql)
        except:
            pass
        cursor.close()

    def guardar_informacion(self):
        cursor = conn.cursor()
        proveeCombustible = str(self.proveeCombustible.text())
        numGlns = int(self.numGlns.text())
        costo = numGlns*3
        # Usar par√°metros en la consulta SQL
        cadena_sql = "INSERT INTO Proveedores (proveeCombustible, numGlns, costoGln) VALUES (?, ?, ?);"
        #(proveeCombustible, numGlns, costo)
        # ejecutar el SQL
        cursor.execute(cadena_sql, (proveeCombustible, numGlns, costo))
        # confirmar los cambios
        conn.commit()
        cursor.close()

    def obtener_informacion(self):
        cursor = conn.cursor()
        cadena_consulta_sql = "SELECT * from Proveedores"
        cursor.execute(cadena_consulta_sql)
        informacion = cursor.fetchall()
        database_table_column_count = 3
        self.listaIngresos.setColumnCount(database_table_column_count)
        numero_filas = len(informacion)
        self.listaIngresos.setRowCount(numero_filas)
        for j in range(numero_filas):
            valor = informacion[j]
            for i in range(0, len(valor)):
                elemento = valor[i]
                elemento = str(elemento)
                nuevo_registro = QtWidgets.QTableWidgetItem(elemento)
                self.listaIngresos.setItem(j, i, nuevo_registro)

